import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { render } from 'react-dom';



function Copyright(props) {
  return (
    <Typography variant="body2" color="text.secondary" align="center" {...props}>
      {'Copyright © '}
        CollegeConnect
      {' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const theme = createTheme();

export default function SignIn() {
  const handleSubmit = (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    // eslint-disable-next-line no-console
    const email = data.get('email')

    //  JSON.parse(result).body

    if(email.includes("coloradocollege")) {
      var express = require('express');
      var session = require('express-session');
      var CAS = require('express-cas');
      
      var cas = new CAS({
        casServerUrlPrefix: 'https://cas.coloradocollege.edu/cas/',
        serverName: 'https://cas.coloradocollege.edu/cas/',
        service: 'https://main.d3vqlqvpmdvby4.amplifyapp.com',
      });
      
      var app = express();
      
      app.use( session({
       secret: 'super secret key',
       resave: false,
       saveUninitialized: true
      }));
      
      // Unauthenticated clients will be redirected to the CAS login 
      // and then back to this route once authenticated.
      app.get('/app', cas.bounce, function( req, res ) {
        res.send( '<html><body>Hello!</body></html>' );
      });
      
      // Unauthenticated clients will receive a 401 
      // Unauthorized response instead of the JSON data.
      app.get('/api', cas.block, function( req, res ) {
        res.json({ success: true });
      });
      
      // An example of accessing the CAS user session variable. 
      // This could be used to retrieve your own local user records
      // based on authenticated CAS username.
      app.get('/api/user', cas.block, function ( req, res ) {
        res.json( { cas_username: req.session[ cas.sessionName ] } );
      });
      
      // Unauthenticated clients will be redirected to the CAS login 
      // and then to the provided "referer" query parameter once authenticated.
      app.get('/authenticate', cas.redirect );
      
      // This route will de-authenticate the client with the Express server 
      // and then redirect the client to the CAS logout page.
      app.get('/logout', cas.logout);
      
    }

    


    
else {
    // alert is working but the text field is not changing in response to the error
    alert("Error: Colorado College email required.");
    <TextField
    error
    id="outlined-error-helper-text"
    label="Error"
    defaultValue=""
    helperText="Colorado College email required."
  />
}

  };

  return (
    
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
            <LockOutlinedIcon />
          </Avatar>
          Sign in
          <Typography component="h1" variant="h5">
          CollegeConnect
          </Typography>
          <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1 }}>
            <TextField
              margin="normal"
              required
              fullWidth
              id="email"
              label="Email Address"
              name="email"
              autoComplete="email"
              autoFocus
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              Sign In
            </Button>
            <a href="home" class="active">Skip Ahead to Home Page</a>
          </Box>
        </Box>
        <Copyright sx={{ mt: 8, mb: 4 }} />
      </Container>
    </ThemeProvider>
  );
}
